import React, { useState, useEffect } from 'react';
import { View, Text, Image, TouchableOpacity } from 'react-native';
import PostagemService from '../Services/PostagemService';
import UsuarioService from '../Services/UsuarioService';

const Index = ({ navigation }) => {

    const [dadosApi, setDadosApi] = useState({
        usuario: null,
        postagem: null,
    });

    useEffect(() => {
        const fetchData = async () => {
            const posts= new PostagemService();
            try {
                const postData = await posts.GetAll();
                setDadosApi((prevDados) => ({ ...prevDados, postagem: postData }));
            } catch (error) {
                console.error('Erro na chamada da API:', error);
            }
        };

        fetchData();
    }, []);

    useEffect(() => {
        const fetchUserData = async () => {
          const users = new UsuarioService();
          try {
            const usersData = await users.GetAll();
            const postagemService = new PostagemService();
            const postagensData = await postagemService.GetAll();
            const usuarioFiltrado = usersData.filter((user) =>
                postagensData.some((postagem) => postagem.autorId === user.id)
                );
            setDadosApi((prevDados) => ({ ...prevDados, usuario: usuarioFiltrado }));
          } catch (error) {
            console.error('Erro ao buscar usuarios:', error);
          }
        };
    
        fetchUserData();
      }, []);

    return (
        <View style={{ flex: 1, padding: 16 }}>
            {dadosApi && (
                <View style={{ flexDirection: 'row', alignItems: 'center' }}>
                    {/* Foto do usu√°rio */}
                    <Image
                        //source={src\img\userImg.png}
                        style={{ width: 50, height: 50, borderRadius: 25, marginRight: 8 }}
                    />

                    {/* Nome do usu√°rio */}
                    <View style={{ flex: 1 }}>
                        <Text style={{ fontWeight: 'bold' }}>{dadosApi.nomeUsuario}</Text>
                    </View>

                    {/* √çcone para chat */}
                    <TouchableOpacity>
                        <Text>üó®Ô∏è</Text>
                    </TouchableOpacity>
                </View>
            )}

            {/* Conte√∫do da postagem */}
            {dadosApi && (
                <View style={{ marginTop: 8 }}>
                    <Text>{dadosApi.conteudoPostagem}</Text>
                </View>
            )}

            {/* √çcone de cora√ß√£o e √≠cone para comentar */}
            {dadosApi && (
                <View style={{ flexDirection: 'row', justifyContent: 'space-between', marginTop: 8 }}>
                    {/* √çcone de cora√ß√£o */}
                    <TouchableOpacity>
                        <Text>‚ù§Ô∏è</Text>
                    </TouchableOpacity>

                    {/* √çcone para comentar */}
                    <TouchableOpacity>
                        <Text>üó®Ô∏è</Text>
                    </TouchableOpacity>
                </View>
            )}
        </View>
    );
}

export default Index;
