import React from 'react';
import { StyleSheet, Text, View, Image, SafeAreaView, ScrollView } from 'react-native';
import Icon from 'react-native-vector-icons/AntDesign';

export default function App() {
    return (
        <SafeAreaView style={styles.container}>
            <ScrollView>
                <View style={styles.wrapper}>
                    <View style={styles.header}>
                        <View style={styles.foto}>
                            <Image style={styles.fotoImg} source={require('../Assets/coelho2.png')} />
                        </View>
                        <View style={styles.leftHeader}>
                            <View style={styles.nomeUser}>
                                <Text style={styles.nome}>Ana Lívia</Text>
                            </View>
                            <View style={styles.box}>
                                <Text style={styles.infoBox}>Informações do usuário, conteúdo referente a autores e estilos favoritos, por exemplo...</Text>
                                <View style={styles.shadow}></View>
                            </View>
                        </View>
                    </View>
                    <View style={styles.livrosDisp}>
                        <View style={styles.iconLivr}>
                            <Icon name='pluscircleo' style={styles.livrDisp} />
                        </View>
                        <View style={styles.textLivr}>
                            <Text style={styles.livrosDisp}>Livros Disponíveis</Text>
                        </View>
                    </View>
                </View>
                <View style={styles.areaLivros}>
                    <View style={styles.linhaLivros}>
                        <View style={styles.livro}>
                            <Image style={styles.imagemLivro} source={require('../img/Livro1.jpg')} />
                        </View>
                        <View style={styles.livro}>
                            <Image style={styles.imagemLivro} source={require('../img/Livro2.jpg')} />
                        </View>
                        <View style={styles.livro}>
                            <Image style={styles.imagemLivro} source={require('../img/Livro3.jpg')} />
                        </View>
                    </View>
                    <View style={styles.linhaLivros}>
                        <View style={styles.livro}>
                            <Image style={styles.imagemLivro} source={require('../img/Livro4.jpg')} />
                        </View>
                        <View style={styles.livro}>
                            <Image style={styles.imagemLivro} source={require('../img/Livro5.jpg')} />
                        </View>
                        <View style={styles.livro}>
                            <Image style={styles.imagemLivro} source={require('../img/Livro6.jpg')} />
                        </View>
                    </View>
                    <View style={styles.linhaLivros}>
                        <View style={styles.livro}>
                            <Image style={styles.imagemLivro} source={require('../img/Livro7.jpg')} />
                        </View>
                        <View style={styles.livro}>
                            <Image style={styles.imagemLivro} source={require('../img/Livro8.jpg')} />
                        </View>
                    </View>
                </View>
            </ScrollView>
        </SafeAreaView>
    );
}

const styles = StyleSheet.create({
    container: {
        flexGrow: 1,
        flexDirection: 'column',
        alignItems: 'center',
        backgroundColor: '#392620',
        padding: 20,
    },
    wrapper: {
        display: 'flex',
        flexDirection: 'column',
        height: '35%'
    },
    header: {
        flexDirection: 'row',
        height: '70%',
    },
    leftHeader: {
        flexDirection: 'column',
        marginLeft: 20,
    },
    foto: {
        width: 100,
        height: 100,
        borderRadius: 75,
        overflow: 'hidden',
    },
    fotoImg: {
        width: '100%',
        height: '100%',
    },
    nomeUser: {
        marginBottom: 10,
    },
    nome: {
        fontSize: 24,
        color: 'darkorange',
        fontWeight: 'bold',
    },
    box: {
        position: 'relative',
    },
    shadow: {
        position: 'absolute',
        backgroundColor: 'black',
        height: '100%',
        width: '100%',
        borderRadius: 5,
        opacity: 0.3,
    },
    infoBox: {
        color: 'black',
        backgroundColor: '#fef5da',
        borderRadius: 5,
        padding: 10,
    },
    livrosDisp: {
        flexDirection: 'row',
        alignItems: 'center',
        height: '30%'
    },
    iconLivr: {
        marginRight: 10,
    },
    textLivr: {
        fontSize: 20,
        color: 'darkorange',
        fontWeight: 'bold',
    },
    areaLivros: {
        flexDirection: 'column',
    },
    linhaLivros: {
        flexDirection: 'row',
        marginBottom: 20,
    },
    livro: {
        flex: 1,
        marginRight: 10,
    },
    imagemLivro: {
        width: '100%',
        height: 130,
        borderRadius: 5,
    },
});
