import React from 'react'
import { View, Text, StyleSheet, TextInput, Pressable, TouchableOpacity, MD3Colors, Image, ScrollView, } from 'react-native';

const Publicacao = ({ navigation }) => {

  return (

    <View style={styles.container}>

      <View style={styles.cabecalho}>
        <Text style={styles.header}> RabbitBook </Text>
      </View>

      <View style={styles.user}>

        <Image source={require('../img/foto.png')}
          style={{ flexDirection: "row", height: 75, width: 75 }} />
        <Text style={styles.nameuser}>  Ana Lívia </Text>

      </View>
      <View>
        <TextInput style={styles.conteudo}
          placeholder="Escrever uma publicação..."
        />

        <TouchableOpacity style={styles.botao1} >

          <Text style={styles.botoes}>Publicar</Text>

        </TouchableOpacity>
      </View>
    </View>

  );

}

const styles = StyleSheet.create({

  container: {
    flex: 1,
    display: 'flex',
    flexDirection: 'column',
    //backgroundColor: "#392620",
    justifyContent: "center"
  },
  cabecalho: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: '#392620',
    height: 100,
  },
  user: {
    flexDirection: 'row',
    margin: 3,
    alignItems: 'flex-start',
    color: "black",
    fontWeight: 'bold',
    fontSize: "20px",
    height: 75,
    backgroundColor: 'rgba(139, 69, 19, 0.5)',
  },
  nameuser: {
    flexDirection: 'row',
    alignSelf: 'center',
    color: "darkorange",
    fontWeight: 'bold',
    fontSize: "20px"
  },
  header: {
    textAlign: "center",
    fontSize: 25,
    color: "darkorange",
    marginBottom: 40,
    marginTop: 30,
  },
  conteudo: {
    width: '80%',
    height: 500,
    margin: 10,
    fontSize: 15,
    marginTop: 10,
    color: "#fef5da",
    textAlign: "top",
  },
  botoes: {
    fontSize: 15,
    color: "darkorange",
    textAlign: "center",
    fontWeight: 'bold',
  },
  botao1: {
    backgroundColor: "#fef5da",
    color: "darkorange",
    width: '100%',
    padding: 10,
    marginBottom: 30,
    borderRadius: 30,
    alignItems: 'center',
  },
})

export default Publicacao;
