import React from 'react'
import { View, Text, StyleSheet, TextInput, Pressable, TouchableOpacity, MD3Colors, Image, ScrollView, } from 'react-native';
import Icon from 'react-native-vector-icons/AntDesign';

const EscolhaChat = ({ navigation }) => {

  const goToChat1 = () => {
    navigation.navigate('chat1');
  }

  return (

    <ScrollView style={styles.scroll}>

      <View style={styles.container}>

        <View style={styles.cabecalho}>
          <Text style={styles.header}> RabbitBook </Text>

          <View style={styles.inputContainer}>
            <Icon name="search1" size={20} color="#808080" style={styles.icon} />
            <TextInput
              style={styles.pesquisa}
              placeholder="Pesquisar"
              borderWidth={0}
              underlineColorAndroid="transparent"
            />
          </View>
        </View>

        <View>

        </View>

      </View>

    </ScrollView>



  );

}

const styles = StyleSheet.create({


  scroll: {
    backgroundColor: "#392620",
  },
  container: {
    flex: 1,
    backgroundColor: "#392620",
    justifyContent: "center"
  },
  cabecalho: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    height: 130,
  },
  icon: {
    paddingLeft: 5
  },
  inputContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    alignSelf: 'center',
    borderBottomWidth: 1,
    borderColor: '#808080',
    width: '50%',
    fontWeight: 'bold',
    marginLeft: 15,
    height: 30,
    fontSize: 15,
    marginTop: 10,
    color: "darkorange",
    backgroundColor: '#fef5da',
    textAlign: "center",
    borderRadius: 75,
  },
  pesquisa: {
    width: '100%',
    height: '100%',
    borderWidth: 0,
    borderBottomWidth: 0,
  },
  header: {
    textAlign: "center",
    fontSize: 30,
    color: "darkorange",
    marginBottom: 10,
    marginTop: 30,
  },
  chat: {
    backgroundColor: "#fef5da",
    color: "darkorange",
    width: '100%',
    marginTop: 50,
    height: 70,
    flexDirection: 'row',
  },
  username: {
    fontSize: 15,
    color: "darkorange",
    textAlign: "center",
    fontWeight: 'bold',
    marginBottom: 1,
  },
  descricao: {
    fontSize: 11,
    color: "grey",
    textAlign: 'left',
  },
  lado: {
    flexDirection: 'row',
  }
})

export default EscolhaChat;
