import React, { useState } from 'react';
import { View, Text, StyleSheet, TextInput, Pressable } from 'react-native';
import { useNavigation } from '@react-navigation/native';
import UsuarioService from '../Services/UsuarioService';
import BibliotecaService from '../Services/BibliotecaService';


const Cadastro = () => {

  const [nome, setNome] = useState('');
  const [email, setEmail] = useState('');
  const [senha, setSenha] = useState('');
  const [isVisible, setIsVisible] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');

  const usuario = new UsuarioService();
  const biblioteca = new BibliotecaService();

  const cadastrar = async (usuarioService, bibliotecaService) => {
    if (!nome || !email || !senha) {
      setErrorMessage('É necessário preencher todos os dados');
      setIsVisible(true);
    }
    else {
      setIsVisible(false);
      const usuarioExiste = await usuarioService.Exist(email);
      if (usuarioExiste) {
        setErrorMessage('Usuário já cadastrado');
        setIsVisible(true);
      }
      else {
        setIsVisible(false);
        const novoUsuario = usuarioService.Post({
          "nome": nome,
          "email": email,
          "senha": senha
        })
        bibliotecaService.Post({
          "usuarioId": novoUsuario.id,
          "livros": []
        })
      }
    }
  };

  const navigation = useNavigation();

  const telaLogin = () => {
    navigation.navigate('Login');
  };

  return (

    <View style={styles.container}>

      <Text style={styles.header}> RabbitBook </Text>

      <TextInput
        style={styles.login1}
        placeholder="Informe o seu Nome"
        autoCapitalize="none"
        value={nome}
        onChangeText={(text) => setNome(text)}
      />

      <TextInput
        style={styles.login2}
        placeholder="Informe o Email"
        keyboardAppearance="email-address"
        autoCapitalize="none"
        autoComplete="email"
        value={email}
        onChangeText={(text) => setEmail(text)}
      />

      <TextInput style={styles.login2}
        placeholder="Informe a Senha"
        autoCapitalize="none"
        secureTextEntry
        value={senha}
        onChangeText={(text) => setSenha(text)}
      />

      <Pressable onPress={() => cadastrar(usuario, biblioteca)} style={styles.botao1} >
        <Text style={styles.botoes}>Cadastrar</Text>
      </Pressable>

      <Pressable onPress={() => telaLogin()}>
        <Text style={styles.text}>Já possuo cadastro</Text>
      </Pressable>

      {isVisible && <Text style={styles.span}>{errorMessage}</Text>}

    </View>

  );

}

const styles = StyleSheet.create({

  container: {
    flex: 1,
    alignItems: 'center',
    backgroundColor: "#2C2C2C",
    justifyContent: "center"
  },
  header: {
    fontSize: 30,
    color: "#1154FF",
    marginBottom: 0,
  },
  botoes: {
    fontSize: 15,
    color: "white",
    textAlign: "center",
  },
  login1: {
    borderColor: "#1154FF",
    borderWidth: 1,
    borderRadius: 10,
    width: '80%',
    padding: 10,
    margin: 20,
    fontSize: 25,
    marginTop: 50,
    textAlign: "center",
    color: "#9D9D9D",
    fontSize: 15,
  },
  login2: {
    borderColor: "#1154FF",
    borderWidth: 1,
    borderRadius: 10,
    width: '80%',
    padding: 10,
    margin: 20,
    fontSize: 25,
    textAlign: "center",
    color: "#9D9D9D",
    fontSize: 15,
  },
  botao1: {
    backgroundColor: "#1154FF",
    width: '80%',
    padding: 10,
    margin: 30,
    borderRadius: 30,
    alignItems: 'center',
  },
  span: {
    display: 'flex',
    fontSize: 15,
    color: '#FF0000',
  },
  text: {
    fontSize: 20,
    color: '#9D9D9D',
  }
})

export default Cadastro;
