import React, { useState, useEffect } from 'react';
import { View, Text } from 'react-native';
import PostagemService from '../Services/PostagemService';

const Index = ({ navigation }) => {

    const [dadosApi, setDadosApi] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            const postagem = new PostagemService();
            try {
                const response = await postagem.Get();
                setDadosApi(response);
            } catch (error) {
                console.error('Erro na chamada da API:', error);
            }
        };

        fetchData();
    }, []);

    return(
        <View style={{ flex: 1, padding: 16 }}>
      {dadosApi && (
        <View style={{ flexDirection: 'row', alignItems: 'center' }}>
          {/* Foto do usu√°rio */}
          <Image
            source={{ uri: dadosApi.fotoUsuario }}
            style={{ width: 50, height: 50, borderRadius: 25, marginRight: 8 }}
          />

          {/* Nome do usu√°rio */}
          <View style={{ flex: 1 }}>
            <Text style={{ fontWeight: 'bold' }}>{dadosApi.nomeUsuario}</Text>
          </View>

          {/* √çcone para chat */}
          <TouchableOpacity>
            <Text>üó®Ô∏è</Text>
          </TouchableOpacity>
        </View>
      )}

      {/* Conte√∫do da postagem */}
      {dadosApi && (
        <View style={{ marginTop: 8 }}>
          <Text>{dadosApi.conteudoPostagem}</Text>
        </View>
      )}

      {/* √çcone de cora√ß√£o e √≠cone para comentar */}
      {dadosApi && (
        <View style={{ flexDirection: 'row', justifyContent: 'space-between', marginTop: 8 }}>
          {/* √çcone de cora√ß√£o */}
          <TouchableOpacity>
            <Text>‚ù§Ô∏è</Text>
          </TouchableOpacity>

          {/* √çcone para comentar */}
          <TouchableOpacity>
            <Text>üó®Ô∏è</Text>
          </TouchableOpacity>
        </View>
      )}
    </View>
    );
}

export default Index;
