import React, { useState } from 'react';
import {View, Text, StyleSheet, TextInput, Pressable} from 'react-native';


const Cadastro = () => {

  const [nome, setNome] = useState('');
  const [email, setEmail] = useState('');
  const [senha, setSenha] = useState('');
  const [isVisible, setIsVisible] = useState(true);

  const cadastrarUsuario = async () => {

    try 
    {
      const response = await fetch(
        "http://localhost:3001/usuarios",
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(
            {
              "nome": nome,
              "email": email,
              "senha": senha
            }
          )
        }
      )
    } 
    catch (error) 
    {
      console.log(error);
    }
  }

  const validacao = () => {
    if (!nome || !email || !senha) {
      setIsVisible(false);
    } else {
      cadastrarUsuario();
    }
  };

  return (

  <View style={styles.container}>
  
  <Text style={styles.header}> RabbitBook </Text>

    <TextInput 
      style={styles.login1} 
      placeholder="Informe o seu Nome"
      autoCapitalize= "none"
      value={nome}
      onChangeText={(text) => setNome(text)}
    />

    <TextInput 
      style={styles.login2} 
      placeholder="Informe o Email"
      keyboardAppearance= "email-address"
      autoCapitalize= "none"
      autoComplete= "email"
      value={email}
      onChangeText={(text) => setEmail(text)}
    />

    <TextInput style={styles.login2}
      placeholder="Informe a Senha"
      autoCapitalize= "none"
      secureTextEntry
      value={senha}
      onChangeText={(text) => setSenha(text)}
    />

    <Pressable onPress={validacao} style={styles.botao1} >
      <Text style={styles.botoes}>Cadastrar</Text>
    </Pressable>

    {isVisible && <Text style={styles.span}>É necessário preencher todos os dados</Text>}

  </View>

);

}

const styles = StyleSheet.create({

  container: {
    flex: 1,
    alignItems: 'center',
    backgroundColor: "#2C2C2C",
    justifyContent: "center"
  },
  header: {
    fontSize: 30,
    color: "#1154FF",
    marginBottom: 0,
  },
  botoes: {
    fontSize: 15,
    color: "white",
    textAlign: "center",
  },
  login1: {
    borderColor: "#1154FF",
    borderWidth: 1,
    borderRadius: 10,
    width: '80%',
    padding: 10,
    margin: 20,
    fontSize: 25,
    marginTop: 50,
    textAlign: "center",
    color: "#9D9D9D",
    fontSize: 15,
    },
  login2: {
    borderColor: "#1154FF",
    borderWidth: 1,
    borderRadius: 10,
    width: '80%',
    padding: 10,
    margin: 20,
    fontSize: 25,
    textAlign: "center",
    color: "#9D9D9D",
    fontSize: 15,
    },
  botao1: {
    backgroundColor: "#1154FF",
    width: '80%',
    padding: 10,
    margin: 30,
    borderRadius: 30,
    alignItems: 'center',
  },
  span:{
    display: 'flex',
    fontSize: 20,
    color: "#9D9D9D",
  }
})

export default Cadastro;
